import React,{useState} from 'react'
// import {
//     FormControl,
//     FormLabel
//   } from "@chakra-ui/react"
import {Formik,Form} from 'formik'
  import { Box, Heading, Input,Button,InputGroup,InputRightElement, Flex} from '@chakra-ui/react'
  import FormikControl from '../FormikControl'
  import {LoginSchema} from '../../validation'

  const  LoginForm = ()=> {

    const [show, setShow] = useState(false)
    const handleClick = () => setShow(!show)

    const initialValues = {
        email: '',
        password: ''
    }

    const onSubmit =values => {
        console.log( 'Data',values)
    }       

    return(
        <Flex justify='center' w={{md:'80%vw'}}>
        <Box bg='gray.200' >
            <Box textAlign='center' my={6}>
                <Heading as='h5' fontSize={{ md: '2xl'}}>LOGIN</Heading>
            </Box>
            <Formik
                initialValues={initialValues}
                validationSchema = {LoginSchema}
                onSubmit= {onSubmit}
            >
                { formik => (
                    <Form>
                        <Box >
                            <Input placeholder="Enter email" variant="flushed"  type='email' name='email' size="lg" my={2} />
                            <InputGroup size="md">
                                <Input
                                variant="flushed"
                                type={show ? "text" : "password"}
                                placeholder="Enter password"
                                />
                                <InputRightElement width="4.5rem">
                                <Button h="1.75rem" size="sm" onClick={handleClick}>
                                    {show ? "Hide" : "Show"}
                                </Button>
                                </InputRightElement>
                            </InputGroup>
                            <Button type='submit' disabled = {!formik.isValid} my={2}></Button>
                        </Box>
                    </Form>
                )}
            </Formik>
        </Box>
        </Flex>
    )

  }

  export default LoginForm
