import React, {useState} from 'react'
// import {
//     FormControl,
//     FormLabel
//   } from "@chakra-ui/react"
import {Formik,Form} from 'formik'
  import { Box, Heading,Input,InputGroup, InputRightElement,Button} from '@chakra-ui/react'
  import {LoginSchema} from '../../validation'

  const  LoginForm = ()=> {

    const [show, setShow ] = useState(false)

    const handleClick=()=> {
        setShow(!show)
    }

    const initialValues = {
        email: '',
        password: ''
    }

    const onSubmit =values => {
        console.log( 'Data',values)
    }

    return(
        <Box>
            <Box textAlign='center' my={6}>
                <Heading as='h5' fontSize={{ md: '2xl'}}>LOGIN</Heading>
            </Box>
            <Formik
                initialValues={initialValues}
                validationSchema = {LoginSchema}
                onSubmit= {onSubmit}
            >
                { formik => (
                    <Form>
                        <Box mx='400px'>
                            <Input 
                                variant="flushed" 
                                name='email' 
                                placeholder="Enter email" 
                                mt={6} 
                                borderColor='black'
                                />
                            <InputGroup size="md" my={4}>
                                <Input
                                variant="flushed"
                                type={show ? "text" : "password"}
                                placeholder="Enter password"
                                borderColor='black'
                                mb={4}
                                />
                                <InputRightElement >
                                <Button h='25px' w='45px' fontSize="10px" rounded='5px' my={4} onClick={handleClick}>
                                    {show ? "Hide" : "Show"}
                                </Button>
                                </InputRightElement>
                            </InputGroup>
                            <Button type='submit' colorScheme='pink' disabled = {!formik.isValid}> Login</Button>
                        </Box>
                    </Form>
                )}
            </Formik>
        </Box>
    )

  }

  export default LoginForm
