import React from 'react'
// import {
//     FormControl,
//     FormLabel
//   } from "@chakra-ui/react"
import {Formik,Form} from 'formik'
  import { Box, Heading, Input,Button, Flex} from '@chakra-ui/react'
  import FormikControl from '../FormikControl'
  import {LoginSchema} from '../../validation'

  const  LoginForm = ()=> {

    const initialValues = {
        email: '',
        password: ''
    }

    const onSubmit =values => {
        console.log( 'Data',values)
    }

    return(
        <Box bg='gray.200'>
            <Box textAlign='center' my={6}>
                <Heading as='h5' fontSize={{ md: '2xl'}}>LOGIN</Heading>
            </Box>
            <Formik
                initialValues={initialValues}
                validationSchema = {LoginSchema}
                onSubmit= {onSubmit}
            >
                { formik => (
                    <Form>
                        <Box bg='white' w='30%' h='60%' >
                            <Flex justify='center'>
                                <Box>
                                <Input placeholder="Email" type='email' name='email' size="lg" />
                                <Input placeholder="Password" type='password' name='password' size="lg" />
                                <Button type='submit' disabled = {!formik.isValid}></Button>
                                </Box>
                            </Flex>
                        </Box>
                    </Form>
                )}
            </Formik>
        </Box>
    )

  }

  export default LoginForm
