import React from 'react'
// import {
//     FormControl,
//     FormLabel
//   } from "@chakra-ui/react"
import {Formik,Form} from 'formik'
  import { Box, Heading, Input,Button, Flex} from '@chakra-ui/react'
  import FormikControl from '../FormikControl'
  import {LoginSchema} from '../../validation'

  const  LoginForm = ()=> {

    const initialValues = {
        email: '',
        password: ''
    }

    const onSubmit =values => {
        console.log( 'Data',values)
    }

    return(
        <Flex justify='center'>
        <Box bg='gray.200' >
            <Box textAlign='center' my={6}>
                <Heading as='h5' fontSize={{ md: '2xl'}}>LOGIN</Heading>
            </Box>
            <Formik
                initialValues={initialValues}
                validationSchema = {LoginSchema}
                onSubmit= {onSubmit}
            >
                { formik => (
                    <Form>
                        <Box w='80%' h='80%' mx={8}>
                            <Input placeholder="Email" variant="flushed"  type='email' name='email' size="lg" my={2} />
                            <Input placeholder="Password" type='password' name='password' size="lg" my={2} />
                            <Button type='submit' disabled = {!formik.isValid} my={2}></Button>
                        </Box>
                    </Form>
                )}
            </Formik>
        </Box>
        </Flex>
    )

  }

  export default LoginForm
