import React,{useState} from 'react'
// import {
//     FormControl,
//     FormLabel
//   } from "@chakra-ui/react"
import {Formik,Form} from 'formik'
  import { Box, Heading, Input,Button,InputGroup,InputRightElement, Flex} from '@chakra-ui/react'
  import FormikControl from '../FormikControl'
  import {LoginSchema} from '../../validation'

  const  LoginForm = ()=> {

    const [show, setShow] = useState(false)
    const handleClick = () => setShow(!show)

    const initialValues = {
        email: '',
        password: ''
    }

    const onSubmit =values => {
        console.log( 'Data',values)
    }       

    return(
        <Flex justify='center'>
        <Box  >
            <Box textAlign='center' my={6}>
                <Heading as='h5' fontSize={{ md: '2xl'}}>LOGIN</Heading>
            </Box>
            <Formik
                initialValues={initialValues}
                validationSchema = {LoginSchema}
                onSubmit= {onSubmit}
            >
                { formik => (
                    <Form>
                        <Box >
                            <Input placeholder="Enter email" borderColor='black' variant="flushed"  type='email' name='email' size="lg" my={2} />
                            <InputGroup size="md">
                                <Input
                                variant="flushed"
                                type={show ? "text" : "password"}
                                placeholder="Enter password"
                                borderColor='black'
                                mb={6}
                                />
                                <InputRightElement width='90px'>
                                <Button h='45px' size="md" onClick={handleClick}>
                                    {show ? "Hide" : "Show"}
                                </Button>
                                </InputRightElement>
                            </InputGroup>
                            <Button type='submit' disabled = {!formik.isValid} my={2}> Login</Button>
                        </Box>
                    </Form>
                )}
            </Formik>
        </Box>
        </Flex>
    )

  }

  export default LoginForm
