import React, {useState} from 'react'
// import {
//     FormControl,
//     FormLabel
//   } from "@chakra-ui/react"
import {Formik,Form} from 'formik'
  import { Box, Heading,Input,InputGroup, InputRightElement,Button} from '@chakra-ui/react'
  import {LoginSchema} from '../../validation'

  const  LoginForm = ()=> {

    const [show, setShow ] = useState(false)

    const handleClick=()=> {
        setShow(!show)
    }

    const initialValues = {
        email: '',
        password: ''
    }

    const onSubmit =values => {
        console.log( 'Data',values)
    }

    return(
        <Box>
            <Box textAlign='center' my={6}>
                <Heading as='h5' fontSize={{ md: '2xl'}}>LOGIN</Heading>
            </Box>
            <Formik
                initialValues={initialValues}
                validationSchema = {LoginSchema}
                onSubmit= {onSubmit}
            >
                { formik => (
                    <Form>
                       <Input variant="flushed" name='email' placeholder="Enter email" />
                       <InputGroup size="md">
                                <Input
                                variant="flushed"
                                type={show ? "text" : "password"}
                                placeholder="Enter password"
                                borderColor='black'
                                mb={4}
                                />
                                <InputRightElement >
                                <Button size="md" rounded='40px' mb={4} onClick={handleClick}>
                                    {show ? "Hide" : "Show"}
                                </Button>
                                </InputRightElement>
                            </InputGroup>
                        <Button type='submit' disabled = {!formik.isValid}></Button>
                    </Form>
                )}
            </Formik>
        </Box>
    )

  }

  export default LoginForm
